(function(){topBarScroll(document.getElementById('top-bar'));theX(document.getElementById('x'));function imagesLoaded(loader){if(!loader)return;var imgs=Array.from(document.querySelectorAll('img'));var promises=imgs.map(function(el){return new Promise(function(ok,ko){if(el.complete){ok();}else{el.addEventListener('load',ok);el.addEventListener('error',ko);}});});Promise.all(promises).then(function(){loader.classList.add('done');});}
function topBarScroll(el,height,dflt=0){if(!el)return;var height=height||el.clientHeight;var prev=window.pageYOffset;window.onscroll=function(){var curr=window.pageYOffset;el.style.top=(prev>curr||curr<=height)?`-${dflt}px`:`-${height}px`;prev=curr;}}
function theX(el){if(!el)return;var x=el.dataset.x.substr(1,11).split('').reverse();el.innerText="+48 "+x.join('').replace(/,/g,' ');el.href="tel:"+el.innerText;}})();